package lesson170720;import java.util.concurrent.locks.Lock;import java.util.concurrent.locks.ReentrantLock;/** * Created by User on 24.07.2017. */public class LockCounter {    private int count = 0;    Lock lock = new ReentrantLock();    public int get(){        lock.lock();        try{        return count;        }finally {            lock.unlock();        }    }    public void inc(){        lock.lock();        try{        count++;        }finally {            lock.unlock();        }    }}